UPDATE
	ADMIN_INFO
SET
	USER_NAME 		= #{userName}
	,TEL 			= #{tel}
	,PHONENUMBER 	= #{phonenumber}
	,EMAIL 			= #{email}
	,EVENT_AGREE 	= #{eventAgree}
	,UPDATEDATE 	= now()

	,USER_PW 		= #{userPw}

WHERE 1=1
	AND SEQUSER = #{sequser}

